from lm_pm_shd
where setl_ind='N'
and ps_perd_no = #{psPerdNo,jdbcType=INTEGER}
and loan_no= #{loanNo,jdbcType=VARCHAR}
and  int_State= #{intState,jdbcType=VARCHAR}
order by ps_perd_no asc

select

from lm_pm_shd
where 1=1
and loan_no= #{loanNo,jdbcType=VARCHAR}
and ps_due_dt =#{psDueDt,jdbcType=VARCHAR}
order by ps_perd_no asc

